mapper.update:
UPDATE TB_BROADCAST_STATISTICS
SET LIKES_COUNT = LIKES_COUNT + 1
WHERE BROADCAST_SEQ = #{broadcastSeq}

UPDATE TB_BROADCAST_STATISTICS
SET LIKES_COUNT = LIKES_COUNT - 1
WHERE BROADCAST_SEQ = #{broadcastSeq}

UPDATE TB_BROADCAST_STATISTICS
SET AVG_VIEWER_COUNT = #{averageViewerCount}
WHERE BROADCAST_SEQ = #{broadcastSeq}

UPDATE TB_BROADCAST_STATISTICS
SET MAX_VIEWER_COUNT = #{maxViewerCount}
WHERE BROADCAST_SEQ = #{broadcastSeq}

UPDATE TB_BROADCAST_STATISTICS
SET VIEWERS_COUNT = #{viewersCount}
WHERE BROADCAST_SEQ = #{broadcastSeq}
