mapper.select:
SELECT BC.BROADCAST_SEQ, BC.BROADCAST_SCHEDULED_TIME, BC.BROADCAST_TITLE, BC.BROADCAST_IMAGE, BC.BROADCAST_SUMMARY, BF.BENEFIT_CONTENT, BP.DISCOUNT_PRICE, BP.DISCOUNT_RATE, PD.LIST_PRICE, CH.CHANNEL_IMAGE, CH.CHANNEL_NM, CH.CHANNEL_SEQ FROM TB_BROADCAST BC JOIN TB_BROADCAST_CATEGORY BCT ON BC.CATEGORY_CD = BCT.CATEGORY_CD JOIN TB_BENEFIT BF ON BC.BROADCAST_SEQ = BF.BROADCAST_SEQ JOIN ( SELECT *, ROW_NUMBER() OVER (PARTITION BY BROADCAST_SEQ ORDER BY PRODUCT_SEQ) AS rn FROM TB_BROADCAST_PRODUCT ) BP ON BC.BROADCAST_SEQ = BP.BROADCAST_SEQ AND BP.rn = 1 JOIN TB_PRODUCT PD ON BP.PRODUCT_SEQ = PD.PRODUCT_SEQ JOIN TB_CHANNEL CH ON BC.CHANNEL_NM = CH.CHANNEL_NM WHERE BC.BROADCAST_SCHEDULED_TIME > NOW()
AND BCT.CATEGORY_CD LIKE #{categoryCd}
ORDER BY BC.BROADCAST_SCHEDULED_TIME;
